import AlertDescription from './AlertDescription.vue'
import AlertRoot from './AlertRoot.vue'
import AlertTitle from './AlertTitle.vue'

export const Alert = {
  /** AlertRoot component */
  Root: AlertRoot,

  /** AlertTitle component */
  Title: AlertTitle,

  /** AlertDescription component */
  Description: AlertDescription,
}

import type { ClassValue } from 'clsx'

import { clsx } from 'clsx'
import { twMerge } from 'tailwind-merge'

export function cn(...inputs: ClassValue[]) {
  return twMerge(clsx(inputs))
}

import type { VariantProps } from 'class-variance-authority'

import { cva } from 'class-variance-authority'

export { default as AlertDescription } from './AlertDescription.vue'
export { default as AlertRoot } from './AlertRoot.vue'
export { default as AlertTitle } from './AlertTitle.vue'
export { Alert } from './namespace'

export const alertVariants = cva(
  `
    relative grid w-full grid-cols-[0_1fr] items-start gap-y-0.5 rounded-lg border px-4 py-3 text-sm
    has-[>svg]:grid-cols-[calc(var(--spacing)*4)_1fr] has-[>svg]:gap-x-3
    [&>svg]:size-4 [&>svg]:translate-y-0.5 [&>svg]:text-current
  `,
  {
    variants: {
      variant: {
        default: 'bg-card text-card-foreground',
        destructive:
          `
            bg-card text-destructive
            *:data-[part=description]:text-destructive/90
            [&>svg]:text-current
          `,
      },
    },
    defaultVariants: {
      variant: 'default',
    },
  },
)

export type AlertVariants = VariantProps<typeof alertVariants>
