import CarouselContent from './CarouselContent.vue'
import CarouselItem from './CarouselItem.vue'
import CarouselNext from './CarouselNext.vue'
import CarouselPrevious from './CarouselPrevious.vue'
import CarouselRoot from './CarouselRoot.vue'

export const Carousel = {
  /** Carousel root provider */
  Root: CarouselRoot,

  /** Carousel scroll content */
  Content: CarouselContent,

  /** Carousel item */
  Item: CarouselItem,

  /** Carousel previous control */
  Previous: CarouselPrevious,

  /** Carousel next control */
  Next: CarouselNext,
}

import type useEmblaCarousel from 'embla-carousel-vue'
import type { EmblaCarouselVueType } from 'embla-carousel-vue'
import type { HTMLAttributes, UnwrapRef } from 'vue'

type CarouselApi = EmblaCarouselVueType[1]
type UseCarouselParameters = Parameters<typeof useEmblaCarousel>
type CarouselOptions = UseCarouselParameters[0]
type CarouselPlugin = UseCarouselParameters[1]

export type UnwrapRefCarouselApi = UnwrapRef<CarouselApi>

export interface CarouselProps {
  opts?: CarouselOptions
  plugins?: CarouselPlugin
  orientation?: 'horizontal' | 'vertical'
}

export interface CarouselEmits {
  (e: 'init-api', payload: UnwrapRefCarouselApi): void
}

export interface WithClassAsProps {
  class?: HTMLAttributes['class']
}

import type { ClassValue } from 'clsx'

import { clsx } from 'clsx'
import { twMerge } from 'tailwind-merge'

export function cn(...inputs: ClassValue[]) {
  return twMerge(clsx(inputs))
}

import { createInjectionState } from '@vueuse/core'
import emblaCarouselVue from 'embla-carousel-vue'
import { computed, onMounted, ref } from 'vue'

import type {
  UnwrapRefCarouselApi as CarouselApi,
  CarouselEmits,
  CarouselProps,
} from './interface'

const [useProvideCarousel, useInjectCarousel] = createInjectionState(
  (props: CarouselProps, emits: CarouselEmits) => {
    const orientation = computed(() => props.orientation ?? 'horizontal')

    const [emblaNode, emblaApi] = emblaCarouselVue({
      ...props.opts,
      axis: orientation.value === 'horizontal' ? 'x' : 'y',
    }, props.plugins)

    const canScrollNext = ref(false)
    const canScrollPrev = ref(false)

    function onSelect(api: CarouselApi) {
      canScrollNext.value = api?.canScrollNext() || false
      canScrollPrev.value = api?.canScrollPrev() || false
    }

    function scrollPrev() {
      emblaApi.value?.scrollPrev()
    }

    function scrollNext() {
      emblaApi.value?.scrollNext()
    }

    onMounted(() => {
      if (!emblaApi.value)
        return

      emblaApi.value.on('init', onSelect)
      emblaApi.value.on('reInit', onSelect)
      emblaApi.value.on('select', onSelect)

      onSelect(emblaApi.value)
      emits('init-api', emblaApi.value)
    })

    return {
      carouselRef: emblaNode,
      carouselApi: emblaApi,
      canScrollPrev,
      canScrollNext,
      scrollPrev,
      scrollNext,
      orientation,
    }
  },
)

function useCarousel() {
  const carouselState = useInjectCarousel()

  if (!carouselState)
    throw new Error('useCarousel must be used within a <CarouselRoot />')

  return carouselState
}

export { useCarousel, useProvideCarousel }
