import EmptyContent from './EmptyContent.vue'
import EmptyDescription from './EmptyDescription.vue'
import EmptyHeader from './EmptyHeader.vue'
import EmptyMedia from './EmptyMedia.vue'
import EmptyRoot from './EmptyRoot.vue'
import EmptyTitle from './EmptyTitle.vue'

export const Empty = {
  /** EmptyRoot component */
  Root: EmptyRoot,

  /** EmptyHeader component */
  Header: EmptyHeader,

  /** EmptyMedia component */
  Media: EmptyMedia,

  /** Content container that holds title and description */
  Content: EmptyContent,

  /** EmptyTitle component */
  Title: EmptyTitle,

  /** EmptyDescription component */
  Description: EmptyDescription,
}

import type { ClassValue } from 'clsx'

import { clsx } from 'clsx'
import { twMerge } from 'tailwind-merge'

export function cn(...inputs: ClassValue[]) {
  return twMerge(clsx(inputs))
}

import type { VariantProps } from 'class-variance-authority'

import { cva } from 'class-variance-authority'

export { default as EmptyContent } from './EmptyContent.vue'
export { default as EmptyDescription } from './EmptyDescription.vue'
export { default as EmptyHeader } from './EmptyHeader.vue'
export { default as EmptyMedia } from './EmptyMedia.vue'
export { default as EmptyRoot } from './EmptyRoot.vue'
export { default as EmptyTitle } from './EmptyTitle.vue'
export { Empty } from './namespace'

export const variant = {
  default: 'bg-transparent',
  icon:
    `
      flex size-10 shrink-0 items-center justify-center rounded-lg bg-muted text-foreground
      [&_svg:not([class*='size-'])]:size-6
    `,
}

export const emptyMediaVariants = cva(
  `
    mb-2 flex shrink-0 items-center justify-center
    [&_svg]:pointer-events-none [&_svg]:shrink-0
  `,
  {
    variants: {
      variant,
    },

    defaultVariants: {
      variant: 'default',
    },
  },
)

export type EmptyMediaVariants = VariantProps<typeof emptyMediaVariants>
