/**
 * Formats a byte count into a human-readable string with units ranging from Bytes to YB.
 *
 * @param bytes The number of bytes to format. Must be a non-negative finite number.
 * @param decimals The number of decimal places to include in the formatted result.
 *                 Defaults to 2 and is clamped to a minimum of 0.
 * @returns A string representing the formatted byte value along with its unit.
 * @throws RangeError if the provided byte count is negative or not a finite number.
 */
export function formatBytes(bytes: number, decimals = 2) {
  if (!Number.isFinite(bytes) || bytes < 0)
    throw new RangeError('bytes must be a non-negative finite number')

  if (bytes === 0)
    return '0 Bytes'

  const k = 1024
  const dm = decimals < 0 ? 0 : decimals
  const sizes = ['Bytes', 'KB', 'MB', 'GB', 'TB', 'PB', 'EB', 'ZB', 'YB']
  const i = Math.floor(Math.log(bytes) / Math.log(k))

  return `${Number.parseFloat((bytes / k ** i).toFixed(dm))} ${sizes[i]}`
}
