import type { CollectionItem } from '@ark-ui/vue/select'

import {
  Select as ArkSelect,
  useSelect as useArkSelect,
  useSelectContext as useArkSelectContext,
} from '@ark-ui/vue/select'
import { computed, ref } from 'vue'

import type {
  ExtendedSelectApi,
  UseSelectProps,
  UseSelectReturn,
} from './context'

import { SelectProvider } from './context'

export { SelectProvider, useSelectContext } from './context'
export { useArkSelectContext }
export const SelectControl = ArkSelect.Control
export type { UseSelectContext, UseSelectProps, UseSelectReturn } from './context'
export { Select } from './namespace'
export { default as SelectClearTrigger } from './SelectClearTrigger.vue'
export { default as SelectContent } from './SelectContent.vue'
export { default as SelectEmpty } from './SelectEmpty.vue'
export { default as SelectIndicator } from './SelectIndicator.vue'
export { default as SelectItem } from './SelectItem.vue'
export { default as SelectItemGroup } from './SelectItemGroup.vue'
export { default as SelectItemGroupLabel } from './SelectItemGroupLabel.vue'
export { default as SelectItemIndicator } from './SelectItemIndicator.vue'
export { default as SelectItemText } from './SelectItemText.vue'
export { default as SelectLabel } from './SelectLabel.vue'
export { default as SelectRoot } from './SelectRoot.vue'
export { default as SelectSeparator } from './SelectSeparator.vue'
export { default as SelectTrigger } from './SelectTrigger.vue'
export { default as SelectValueText } from './SelectValueText.vue'
export { createListCollection } from '@ark-ui/vue/select'

// Note: use for standalone composition or RootProvider usage.
export function useSelect<T extends CollectionItem>(
  props?: UseSelectProps<T>,
): UseSelectReturn<T> {
  const api = useArkSelect<T>(props ?? ({} as UseSelectProps<T>))
  const loading = ref(false)
  const nativeInvalid = ref(false)
  const disabled = computed(() => api.value.disabled || loading.value)

  function setNativeInvalid(value: boolean = true) {
    nativeInvalid.value = value
  }

  const extended = computed<ExtendedSelectApi<T>>(() => ({
    ...api.value,
    ...(props ?? {}),
    loading: loading.value,
    disabled: disabled.value,
    invalid: props?.invalid || nativeInvalid.value,
    nativeInvalid: nativeInvalid.value,
    setNativeInvalid,
  }))

  SelectProvider(extended)

  return extended
}

import type { MaybeRefOrGetter } from 'vue'

import { camelize, computed, getCurrentInstance, toRef } from 'vue'

interface PropOptions {
  type?: any
  required?: boolean
  default?: any
}

/**
 * The `useForwardProps` function in TypeScript takes in a set of props and returns
 * a computed value that combines default props with assigned props from the
 * current instance.
 *
 * @param {T} props - The `props` parameter is an object that represents the props
 *                    passed to a component.
 * @returns computed value that combines the default props, preserved props, and
 *          assigned props.
 *
 * **Attribution to the Radix Vue team**
 *
 * Docs: https://www.radix-vue.com/utilities/use-forward-props.html
 *
 * Source: https://github.com/unovue/reka-ui/blob/v2/packages/core/src/shared/useForwardProps.ts
 */
export function useForwardProps<T extends Record<string, any>>(props: MaybeRefOrGetter<T>) {
  const vm = getCurrentInstance()

  // Default value for declared props
  const defaultProps = Object.keys(vm?.type.props ?? {}).reduce((prev, curr) => {
    const defaultValue = (vm?.type.props[curr] as PropOptions).default
    if (defaultValue !== undefined)
      prev[curr as keyof T] = defaultValue

    return prev
  }, {} as T)

  const refProps = toRef(props)

  return computed(() => {
    const preservedProps = {} as T
    const assignedProps = vm?.vnode.props ?? {}

    Object.keys(assignedProps).forEach((key) => {
      preservedProps[camelize(key) as keyof T] = assignedProps[key]
    })

    // Only return value from the props parameter
    return Object.keys({ ...defaultProps, ...preservedProps }).reduce((prev, curr) => {
      if (refProps.value[curr] !== undefined)
        prev[curr as keyof T] = refProps.value[curr]

      return prev
    }, {} as T)
  })
}

import type { ComponentPublicInstance } from 'vue'

import { computed, getCurrentInstance, ref } from 'vue'

import { unrefElement } from './unrefElement'

function isElement(el: any): el is Element {
  return Object.prototype.hasOwnProperty.call(el, 'nodeName') && typeof el.nodeName === 'string'
}

/**
 * Forward component's exposed `value`, `props` and `$el`
 *
 * **Attribution to the Radix Vue team**
 *
 * Docs: https://reka-ui.com/docs/utilities/use-forward-expose#useforwardexpose
 *
 * Source: https://github.com/radix-vue/radix-vue/blob/main/packages/radix-vue/src/shared/useForwardExpose.ts
 */
export function useForwardExpose() {
  // biome-ignore lint/style/noNonNullAssertion: intentional
  const instance = getCurrentInstance()!

  const currentRef = ref<Element | ComponentPublicInstance | null>()

  const currentElement = computed<HTMLElement>(() => {
    // $el could be text/comment for non-single root normal or text root, thus we retrieve the nextElementSibling
    // @ts-expect-error ignore ts error
    return ['#text', '#comment'].includes(currentRef.value?.$el.nodeName)
      // @ts-expect-error ignore ts error
      ? currentRef.value?.$el.nextElementSibling
      // @ts-expect-error ignore ts error
      : unrefElement(currentRef)
  })

  // localExpose should only be assigned once else will create infinite loop

  const localExpose: Record<string, any> | null = Object.assign({}, instance.exposed)

  const ret: Record<string, any> = {}

  // retrieve props for current instance
  for (const key in instance.props) {
    Object.defineProperty(ret, key, {
      enumerable: true,
      configurable: true,
      get: () => instance.props[key],
    })
  }

  // retrieve default exposed value
  if (Object.keys(localExpose).length > 0) {
    for (const key in localExpose) {
      Object.defineProperty(ret, key, {
        enumerable: true,
        configurable: true,
        // biome-ignore lint/style/noNonNullAssertion: intentional
        get: () => localExpose![key],
      })
    }
  }

  // retrieve original first root element
  Object.defineProperty(ret, '$el', {
    enumerable: true,
    configurable: true,
    get: () => instance.vnode.el,
  })

  instance.exposed = ret

  function forwardRef(ref: Element | ComponentPublicInstance | null) {
    currentRef.value = ref

    if (isElement(ref) || !ref)
      return

    // retrieve the forwarded element
    Object.defineProperty(ret, '$el', {
      enumerable: true,
      configurable: true,
      get: () => ref.$el,
    })

    instance.exposed = ret
  }

  return { forwardRef, currentRef, currentElement }
}

import type { ClassValue } from 'clsx'

import { clsx } from 'clsx'
import { twMerge } from 'tailwind-merge'

export function cn(...inputs: ClassValue[]) {
  return twMerge(clsx(inputs))
}

import type {
  CollectionItem,
  UseSelectProps as UseArkSelectProps,
  UseSelectReturn as UseArkSelectReturn,
} from '@ark-ui/vue/select'
import type { ComputedRef } from 'vue'

import { createContext } from '@/composables/createContext'

export interface UseSelectProps<T extends CollectionItem>
  extends UseArkSelectProps<T> {
  loading?: boolean
}

type ArkSelectApi<T extends CollectionItem>
  = UseArkSelectReturn<T> extends ComputedRef<infer R> ? R : never

export type ExtendedSelectApi<T extends CollectionItem> = ArkSelectApi<T> & {
  loading: boolean
  disabled: boolean
  /** Reflects the invalid state reported by the native select element. */
  nativeInvalid: boolean
  setNativeInvalid: (value?: boolean) => void
}

export type UseSelectReturn<T extends CollectionItem>
  = ComputedRef<ExtendedSelectApi<T>>

export interface UseSelectContext<T extends CollectionItem>
  extends UseSelectReturn<T> {}

export const [SelectProvider, useSelectContext]
  = createContext<UseSelectContext<CollectionItem>>('SelectContext')
